import React, {Component} from 'react';
import './App.css';

class App extends Component{
    constructor(props){
        super(props)
        this.state ={
            nounOne: "",
            pluralNoun: "",
            nounTwo: "",
            place: "",
            adjective: "",
            nounThree: ""
        }
    }
    updateNounOne = (event) => {
        this.setState({ nounOne: event.target.value})
    }
    updatePluralNoun = (event) => {
        this.setState({ pluralNoun: event.target.value})
    }
    updateNounTwo = (event) => {
        this.setState({ nounTwo: event.target.value})
    }
    updatePlace = (event) => {
        this.setState({ place: event.target.value})
    }
    updateAdjective = (event) => {
        this.setState({ adjective: event.target.value})
    }
    updateNounThree = (event) => {
        this.setState({ nounThree: event.target.value})
    }
    render(){
        return(
            <div>
            <ul>
                <li>
                    <input
                        value={this.state.nounOne}
                        onChange={this.updateNounOne}
                    />
                </li>
                <input
                    value={this.state.pluralNoun}
                    onChange={this.updatePluralNoun}
                />
                <input
                    value={this.state.nounTwo}
                    onChange={this.updateNounTwo}
                />
                <input
                    value={this.state.place}
                    onChange={this.updatePlace}
                />
                <input
                    value={this.state.adjective}
                    onChange={this.updateAdjective}
                />
                <input
                    value={this.state.nounThree}
                    onChange={this.updateNounThree}
                />
                </ul>
            </div>
        )
    }
}
export default App;
